import React, { useState, useEffect } from "react";
import { useAuth } from "./AuthContext";
import { Shield, LogOut, Users, Settings, Home, Edit2, Trash2, UserPlus, Link2, Search, ArrowDownUp } from "lucide-react"; // Added Search and ArrowDownUp

const API_URL = "http://127.0.0.1:8000/api";

const AdminDashboard = () => {
  const { user, logout, token } = useAuth();
  const [activeTab, setActiveTab] = useState("apps");
  const [users, setUsers] = useState([]);
  const [apps, setApps] = useState([]);
  
  // === NEW STATE FOR AUTHORIZED USERS MANAGEMENT ===
  const [searchQuery, setSearchQuery] = useState("");
  const [sortKey, setSortKey] = useState("email_asc"); // 'email_asc' or 'email_desc'
  // ================================================

  // App form state
  const [newApp, setNewApp] = useState({ 
    name: "", 
    url: "", 
    client_id: "", 
    client_secret: "" 
  });
  const [editingApp, setEditingApp] = useState(null);
  
  // Mapping state
  const [showMapModal, setShowMapModal] = useState(false);
  const [selectedApp, setSelectedApp] = useState(null);
  const [mappingEmail, setMappingEmail] = useState("");

  // Load users and apps from backend (Unchanged)
  useEffect(() => {
    if (!token) return;
    loadUsers();
    loadApps();
  }, [token]);

  const loadUsers = async () => { /* ... (Unchanged) ... */ };
  const loadApps = async () => { /* ... (Unchanged) ... */ };

  // Add, Update, Delete App Handlers (Unchanged)
  const handleAddApp = async (e) => { /* ... (Unchanged) ... */ };
  const handleUpdateApp = async (e) => { /* ... (Unchanged) ... */ };
  const handleDeleteApp = async (id) => { /* ... (Unchanged) ... */ };

  // Map/Unmap User Handlers (Minor change to use new state structure for simplicity)
  const handleMapUser = async () => { /* ... (Unchanged) ... */ };
  const handleUnmapUser = async (email, appId) => {
    if (!confirm(`Remove access for ${email}?`)) return;
    
    try {
      const res = await fetch(`${API_URL}/unmap`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({
          email: email,
          app_id: appId
        }),
      });
      
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      
      await loadApps();
      alert("User access removed!");
    } catch (err) {
      console.error("Error unmapping user:", err);
      alert("Failed to remove access");
    }
  };

  // Update user role (Unchanged)
  const handleRoleChange = async (userId, newRole) => { /* ... (Unchanged) ... */ };

  const stats = [
    { label: "Total Users", value: users.length.toString(), icon: Users, color: "bg-blue-500" },
    { label: "Active Sessions", value: users.filter(u => u.status === "active").length.toString(), icon: Shield, color: "bg-green-500" },
    { label: "Applications", value: apps.length.toString(), icon: Home, color: "bg-purple-500" },
    { label: "Admin Users", value: users.filter(u => u.role === "admin").length.toString(), icon: Settings, color: "bg-orange-500" },
  ];

  // === NEW RENDER FUNCTION WITH SEARCH/SORT LOGIC ===
  const renderAuthorizedUsers = (app) => {
    if (!app.authorized_emails || app.authorized_emails.length === 0) {
      return null;
    }

    const filteredEmails = app.authorized_emails.filter(email =>
      email.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const sortedEmails = [...filteredEmails].sort((a, b) => {
      if (sortKey === 'email_asc') {
        return a.localeCompare(b);
      } else if (sortKey === 'email_desc') {
        return b.localeCompare(a);
      }
      return 0; // Should not happen
    });
    
    const toggleSort = () => {
      setSortKey(prev => prev === 'email_asc' ? 'email_desc' : 'email_asc');
    };

    return (
      <div className="mt-3 pt-3 border-t border-gray-200">
        <div className="flex justify-between items-center mb-2">
          <p className="text-sm font-medium text-gray-700">Authorized Users ({app.authorized_emails.length})</p>
        </div>

        {/* Compact Search and Sort Controls */}
        <div className="flex gap-2 mb-3">
          {/* Search Input */}
          <div className="relative flex-1">
            <input
              type="text"
              placeholder="Search email..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-3 py-1.5 pl-8 border border-gray-300 rounded-lg focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 text-sm"
            />
            <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400" size={14} />
          </div>

          {/* Sort Button */}
          <button
            onClick={toggleSort}
            className="flex items-center gap-1 px-3 py-1.5 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition text-sm border border-gray-300"
            title="Toggle Sort Order"
          >
            <ArrowDownUp size={14} />
            {sortKey === 'email_asc' ? 'A-Z' : 'Z-A'}
          </button>
        </div>

        {/* Display List of Filtered/Sorted Users (with max height for scrolling) */}
        {sortedEmails.length > 0 ? (
          <div className="flex flex-wrap gap-2 max-h-36 overflow-y-auto p-2 border border-gray-100 rounded-lg bg-white">
            {sortedEmails.map((email) => (
              <span
                key={email}
                className="inline-flex items-center gap-2 px-3 py-1 bg-green-100 text-green-700 rounded-full text-xs font-medium"
              >
                {email}
                <button
                  onClick={() => handleUnmapUser(email, app.id)}
                  className="hover:text-red-600 transition"
                  title="Remove access"
                >
                  ×
                </button>
              </span>
            ))}
          </div>
        ) : (
          <p className="text-sm text-gray-500 p-2 border border-gray-100 rounded-lg bg-white">
            {searchQuery ? "No users match your search." : "No authorized users currently assigned."}
          </p>
        )}
      </div>
    );
  };
  // ========================================================
  
  return (
    <div className="min-h-screen bg-gray-100">
      {/* HEADER (Modified to be simpler) */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="bg-purple-600 w-8 h-8 rounded-full flex items-center justify-center">
              <Shield className="text-white" size={16} />
            </div>
            <div>
              <h1 className="text-lg font-bold text-gray-800">Admin Dashboard</h1>
              <p className="text-xs text-gray-500">SSO Management Portal</p>
            </div>
          </div>
          <button
            onClick={logout}
            className="flex items-center gap-1 text-red-600 hover:text-red-700 transition text-sm"
          >
            <LogOut size={16} />
            Logout
          </button>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Stats (Unchanged from your original code's style) */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {stats.map((stat, idx) => (
            <div key={idx} className="bg-white rounded-xl shadow-md p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-500 text-sm">{stat.label}</p>
                  <p className="text-3xl font-bold text-gray-800 mt-1">{stat.value}</p>
                </div>
                <div className={`${stat.color} w-12 h-12 rounded-lg flex items-center justify-center`}>
                  <stat.icon className="text-white" size={24} />
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Tabs (Restored to the original simple tab bar) */}
        <div className="bg-white rounded-xl shadow-md">
          <div className="border-b border-gray-200">
            <div className="flex gap-4 px-6">
              <button
                onClick={() => setActiveTab("users")}
                className={`py-4 px-2 border-b-2 font-medium transition ${
                  activeTab === "users" ? "border-indigo-600 text-indigo-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                User Management
              </button>
              <button
                onClick={() => setActiveTab("roles")}
                className={`py-4 px-2 border-b-2 font-medium transition ${
                  activeTab === "roles" ? "border-indigo-600 text-indigo-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                Role Assignment
              </button>
              <button
                onClick={() => setActiveTab("apps")}
                className={`py-4 px-2 border-b-2 font-medium transition ${
                  activeTab === "apps" ? "border-indigo-600 text-indigo-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                Manage Applications
              </button>
            </div>
          </div>

          <div className="p-6">
            {/* USERS TAB (Unchanged) */}
            {activeTab === "users" && (
              <div>
                <h2 className="text-lg font-bold text-gray-800 mb-4">Registered Users</h2>
                {/* ... (User table content) ... */}
              </div>
            )}

            {/* ROLES TAB (Unchanged) */}
            {activeTab === "roles" && (
              <div>
                <h2 className="text-lg font-bold text-gray-800 mb-4">Role Management</h2>
                {/* ... (Role content) ... */}
              </div>
            )}

            {/* APPLICATIONS TAB */}
            {activeTab === "apps" && (
              <div>
                <h2 className="text-lg font-bold text-gray-800 mb-4">Manage Applications</h2>

                {/* Add/Edit App Form (Unchanged) */}
                <form onSubmit={editingApp ? handleUpdateApp : handleAddApp} className="space-y-3 mb-6 p-4 bg-gray-50 rounded-lg">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    <input
                      type="text"
                      placeholder="Application Name *"
                      value={editingApp ? editingApp.name : newApp.name}
                      onChange={(e) => editingApp 
                        ? setEditingApp({...editingApp, name: e.target.value})
                        : setNewApp({...newApp, name: e.target.value})
                      }
                      className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      required
                    />
                    <input
                      type="url"
                      placeholder="Application URL *"
                      value={editingApp ? editingApp.url : newApp.url}
                      onChange={(e) => editingApp 
                        ? setEditingApp({...editingApp, url: e.target.value})
                        : setNewApp({...newApp, url: e.target.value})
                      }
                      className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      required
                    />
                    <input
                      type="text"
                      placeholder="Client ID (optional)"
                      value={editingApp ? editingApp.client_id : newApp.client_id}
                      onChange={(e) => editingApp 
                        ? setEditingApp({...editingApp, client_id: e.target.value})
                        : setNewApp({...newApp, client_id: e.target.value})
                      }
                      className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                    />
                    <input
                      type="password"
                      placeholder="Client Secret (optional)"
                      value={editingApp ? editingApp.client_secret : newApp.client_secret}
                      onChange={(e) => editingApp 
                        ? setEditingApp({...editingApp, client_secret: e.target.value})
                        : setNewApp({...newApp, client_secret: e.target.value})
                      }
                      className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                    />
                  </div>
                  <div className="flex gap-2">
                    <button type="submit" className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700">
                      {editingApp ? "Update Application" : "Add Application"}
                    </button>
                    {editingApp && (
                      <button 
                        type="button" 
                        onClick={() => setEditingApp(null)}
                        className="bg-gray-300 text-gray-700 px-6 py-2 rounded-lg hover:bg-gray-400"
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </form>

                {/* Applications List */}
                <div className="space-y-4">
                  {apps.map((app) => (
                    <div key={app.id} className="border border-gray-200 rounded-lg p-4">
                      <div className="flex items-start justify-between mb-3">
                        <div className="flex-1">
                          <h3 className="font-semibold text-gray-800 text-lg">{app.name}</h3>
                          <p className="text-sm text-gray-500">{app.url}</p>
                          {app.client_id && (
                            <p className="text-xs text-gray-400 mt-1">Client ID: {app.client_id}</p>
                          )}
                        </div>
                        <div className="flex gap-2">
                          <button
                            onClick={() => setEditingApp(app)}
                            className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                            title="Edit"
                          >
                            <Edit2 size={18} />
                          </button>
                          <button
                            onClick={() => {
                              setSelectedApp(app);
                              setShowMapModal(true);
                            }}
                            className="p-2 text-green-600 hover:bg-green-50 rounded-lg"
                            title="Map User"
                          >
                            <UserPlus size={18} />
                          </button>
                          <button
                            onClick={() => handleDeleteApp(app.id)}
                            className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                            title="Delete"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      </div>
                      
                      {/* Authorized Users - CALLING NEW RENDER FUNCTION */}
                      {renderAuthorizedUsers(app)}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Map User Modal (Unchanged) */}
      {showMapModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          {/* ... (Modal content) ... */}
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
